import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addItem, deleteItem } from './redux/actions';
import styled from "styled-components";


const mapStateToProps = (state) => {
    return (
        { wishList: state.wishList }
    )
}

const mapDispatchToProps = { addItem, deleteItem };

const Box = styled.div`
    width: 400px;
    height: 540px;
    background-color: #FAC0CB;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content : space-around;
    border-radius: 15px;
    box-shadow: 0px 0px 15px ;
`
const Display = styled.div`
    width: 75%;
    height: 50%;
    background-color: white;
    border: 1px solid black;
    box-shadow: 0px 0px 2px inset;

`
const TextBox = styled.input`
    width: 75%;
    height: 5%;
    background-color: white;
    border: 1px solid black;
    box-shadow: 0px 0px 2px inset;
    border-radius: 5px;
`
const Button = styled.a`
    width: ${props => props.add ? "35%" : "75%"} ;
    height: 7%;
    background-color: #91EB92;
    border: 1px solid black;
    box-shadow: 0px 0px 2px inset;
    border-radius: 5px;
    display : flex;
    align-items: center;
    justify-content : center;

    &:hover {
        cursor: pointer;
    }
`
const App = (props) => {
    const [item, setItem] = useState("");

    return (
        <Box>
            <h2>MY WISHLIST</h2>
            <Display>{props.wishList.map(item =>
                <p>{item}</p>
            )}</Display>
            <TextBox onChange={e => setItem(e.target.value)} />
            <Button add onClick={() => props.addItem(item)}>Add</Button>
            <Button>Submit</Button>
        </Box >
    )
}




export default connect(mapStateToProps, mapDispatchToProps)(App);
